mapper.update:
update emp
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, image = #{image}, job = #{job}, dept_id = #{deptId}, entrydate = #{entrydate}, update_time = #{updateTime}
where id = #{id}

mapper.delete:
delete from emp
where id in

    #{id}

mapper.select:
select * from emp

    name like concat('%', #{name}, '%')
    and gender = #{gender} 
    and entrydate between #{begin} and #{end}
